plugins {
    // Apply the application plugin to add support for building a CLI application in Java.
    application
}

repositories {
    // Use Maven Central for resolving dependencies.
    mavenCentral()
}

dependencies {
    // This dependency is used by the application.
    implementation("com.google.guava:guava:30.1.1-jre")
    // Android, Corda DJVM, Java 7+
    implementation("com.hedera.hashgraph:sdk-jdk7:2.0.4")
    implementation("io.grpc:grpc-netty-shaded:1.35.0")
    implementation("org.slf4j:slf4j-simple:1.7.29")

    implementation("com.hedera.hashgraph:sdk:2.0.3")
    implementation("io.grpc:grpc-netty-shaded:1.35.0")
    implementation("io.github.cdimascio:java-dotenv:5.2.1") // Module that allows you to access environment variables from a .env file
}

testing {
    suites {
        // Configure the built-in test suite
        val test by getting(JvmTestSuite::class) {
            // Use JUnit Jupiter test framework
            useJUnitJupiter("5.7.2")
        }
    }
}

application {
    // Define the main class for the application.
    mainClass.set("RmHederaSetup.App")
}
